{"filter":false,"title":"nimotu.rb","tooltip":"/nimotu.rb","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":29,"column":0},"action":"insert","lines":["# 最大積載量","max_capacity = 5000","","# 荷物の重さ（降順）","packages = (1..600).to_a.reverse","","# トラックの数を数える変数","truck_count = 0","","# 現在のトラックの積載量","current_truck_load = 0","","# 荷物を一つずつチェックして積み込む","packages.each do |weight|","  # 現在のトラックに積めるかどうかをチェック","  if current_truck_load + weight <= max_capacity","    # 積める場合は積載量に加算","    current_truck_load += weight","  else","    # 積めない場合は新しいトラックを使用して、積載量を初期化","    truck_count += 1","    current_truck_load = weight","  end","end","","# 最後のトラックもカウント","truck_count += 1 unless current_truck_load == 0","","puts \"#{truck_count}\"",""],"id":1}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":29,"column":0},"end":{"row":29,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1706101736498,"hash":"41eb42a44922f2030a32d63ac47ac4dd83fe7831"}